sources.styles.scss:
*{
    margin: 0px;
    padding: 0px;
    font-family: Karla;

    section{
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: center;
        align-items: baseline;
        align-content: center;
        flex-basis: 100%;
        height: auto;
        

        .bigger{
            flex-grow: 2;
            flex-basis: 100%;
            height: 18rem;
            order: 1;
            align-self:center;
            flex-shrink: 50%;
            text-align: left;
            background-color: #FFFF;
            border: 3px solid #D8FFFF;

            .text{
                display: flex;
                flex-direction: column;
                flex-wrap: wrap;
                border: 1px solid orange;
                margin: 30px;
                margin-top: 50px;
                margin-bottom: 50px;
                text-align: center;


                h1{
                    color: #2BB3B1;
                    margin-bottom: 30px;
                    font-size: 32px;
                }
    
                h3{
                    color: hsl(71, 73%, 54%);
                    margin-bottom: 10px;
                    font-size: 25px;
                }
    
                p{
                    color: #B5BCC4;
                    margin-bottom: 5px;
                    font-size: 18px;
                }
            }

            
        }


        .small1{
            flex-grow: 1;
            flex-basis: 390px;
            height: 20.30rem;
            order:2;
            text-align: left;
            background-color: #2BB3B1;
            border: 3px solid #28ABA7;
            color: white;
            

            .textSmall1{
                border: 1px solid blue;
                margin-top: 50px;
                margin-bottom: 50px;
                text-align: center;
                margin-bottom: 30px;

                h3{
                    margin-bottom: 30px;
                    font-size: 32px;
                }

                span{
                    font-size: 40px;
                }

                button{
                    width: 290px;
                    background-color: hsl(71, 73%, 54%);
                }
            }
            
        }

        .small2{
            flex-grow: 1;
            flex-basis: 390px;
            height: 20.30rem;
            order: 3;
            text-align: left;
            background-color: #78DAD9;
            border: 3px solid #65B9B9;
            color: white;
            
            .textSmall2{
                border: 1px solid blue;
                margin-top: 50px;
                margin-bottom: 50px;
                text-align: center;

                h3{
                    margin-bottom: 30px;
                    font-size: 32px;
                }

                ul{
                    list-style: none;
                }
            }
            
        }
    }
    

